import { useState, useEffect, useRef } from "react";
import { Clock } from "lucide-react";

function pluralize(val, unit) {
    return `${String(val).padStart(2, "0")}`;
}

export default function ContestTimer({ endTime, onExpire }) {
    const [timeLeft, setTimeLeft] = useState(null);
    const onExpireRef = useRef(onExpire);
    onExpireRef.current = onExpire;

    useEffect(() => {
        const end = new Date(endTime).getTime();

        const tick = () => {
            const diff = end - Date.now();
            if (diff <= 0) {
                setTimeLeft(0);
                onExpireRef.current?.();
                return null; // stop
            }
            setTimeLeft(diff);
            return diff;
        };

        const remaining = tick();
        if (remaining === null || remaining <= 0) return;

        const interval = setInterval(() => {
            const r = tick();
            if (r === null || r <= 0) clearInterval(interval);
        }, 1000);

        return () => clearInterval(interval);
    }, [endTime]);

    if (timeLeft === null) return null;

    const totalSecs = Math.floor(timeLeft / 1000);
    const hours = Math.floor(totalSecs / 3600);
    const minutes = Math.floor((totalSecs % 3600) / 60);
    const seconds = totalSecs % 60;

    // Color based on remaining fraction
    const endMs = new Date(endTime).getTime();
    const totalMs = endMs - (endMs - timeLeft); // we don't have start, use fraction of timeLeft directly
    const fraction = timeLeft / (endMs - Date.now() + timeLeft);
    // Simple approach: color by absolute time remaining
    let colorClass = "text-success";
    if (totalSecs < 600) colorClass = "text-danger";
    else if (totalSecs < 1800) colorClass = "text-warning";

    if (timeLeft === 0) {
        return (
            <div className="flex items-center gap-2 badge badge-danger">
                <Clock size={14} />
                <span className="font-mono font-bold">TIME UP</span>
            </div>
        );
    }

    return (
        <div className={`flex items-center gap-2 font-mono font-bold text-base ${colorClass}`}>
            <Clock size={15} className="shrink-0" />
            <span>
                {pluralize(hours)}:{pluralize(minutes)}:{pluralize(seconds)}
            </span>
        </div>
    );
}

import { useEffect, useState, useRef } from "react";
import { motion } from "motion/react";

const styles = {
  wrapper: {
    display: "inline-block",
    whiteSpace: "pre-wrap",
  },
  srOnly: {
    position: "absolute",
    width: "1px",
    height: "1px",
    padding: 0,
    margin: "-1px",
    overflow: "hidden",
    clip: "rect(0,0,0,0)",
    border: 0,
  },
};

export default function DecryptedText({
  text,
  speed = 50,
  maxIterations = 10,
  sequential = false,
  revealDirection = "start",
  useOriginalCharsOnly = false,
  characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz!@#$%^&*()_+",
  className = "",
  parentClassName = "",
  encryptedClassName = "",
  animateOn = "hover",
  ...props
}) {
  const [displayText, setDisplayText] = useState(text);
  const [isHovering, setIsHovering] = useState(false);
  const [isScrambling, setIsScrambling] = useState(false);
  const [revealedIndices, setRevealedIndices] = useState(new Set());
  const [hasAnimated, setHasAnimated] = useState(false);
  const containerRef = useRef(null);

  useEffect(() => {
    let interval;
    let currentIteration = 0;

    const getNextIndex = (revealedSet) => {
      const textLength = text.length;
      switch (revealDirection) {
        case "start":
          return revealedSet.size;
        case "end":
          return textLength - 1 - revealedSet.size;
        case "center": {
          const middle = Math.floor(textLength / 2);
          const offset = Math.floor(revealedSet.size / 2);
          const nextIndex =
            revealedSet.size % 2 === 0 ? middle + offset : middle - offset - 1;

          if (
            nextIndex >= 0 &&
            nextIndex < textLength &&
            !revealedSet.has(nextIndex)
          ) {
            return nextIndex;
          }

          for (let i = 0; i < textLength; i++) {
            if (!revealedSet.has(i)) return i;
          }
          return 0;
        }
        default:
          return revealedSet.size;
      }
    };

    const availableChars = useOriginalCharsOnly
      ? Array.from(new Set(text.split("")).values()).filter((char) => char !== " ")
      : characters.split("");

    const shuffleText = (originalText, currentRevealed) => {
      if (useOriginalCharsOnly) {
        const positions = originalText.split("").map((char, i) => ({
          char,
          isSpace: char === " ",
          index: i,
          isRevealed: currentRevealed.has(i),
        }));

        const nonSpaceChars = positions
          .filter((p) => !p.isSpace && !p.isRevealed)
          .map((p) => p.char);

        for (let i = nonSpaceChars.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [nonSpaceChars[i], nonSpaceChars[j]] = [
            nonSpaceChars[j],
            nonSpaceChars[i],
          ];
        }

        let charIndex = 0;
        return positions
          .map((p) => {
            if (p.isSpace) return " ";
            if (p.isRevealed) return originalText[p.index];
            return nonSpaceChars[charIndex++];
          })
          .join("");
      }

      return originalText
        .split("")
        .map((char, i) => {
          if (char === " ") return " ";
          if (currentRevealed.has(i)) return originalText[i];
          return availableChars[Math.floor(Math.random() * availableChars.length)];
        })
        .join("");
    };

    if (isHovering) {
      setIsScrambling(true);
      interval = setInterval(() => {
        setRevealedIndices((prevRevealed) => {
          if (sequential) {
            if (prevRevealed.size < text.length) {
              const nextIndex = getNextIndex(prevRevealed);
              const newRevealed = new Set(prevRevealed);
              newRevealed.add(nextIndex);
              setDisplayText(shuffleText(text, newRevealed));
              return newRevealed;
            }
            clearInterval(interval);
            setIsScrambling(false);
            return prevRevealed;
          }

          setDisplayText(shuffleText(text, prevRevealed));
          currentIteration++;
          if (currentIteration >= maxIterations) {
            clearInterval(interval);
            setIsScrambling(false);
            setDisplayText(text);
          }
          return prevRevealed;
        });
      }, speed);
    } else {
      setDisplayText(text);
      setRevealedIndices(new Set());
      setIsScrambling(false);
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [
    isHovering,
    text,
    speed,
    maxIterations,
    sequential,
    revealDirection,
    characters,
    useOriginalCharsOnly,
  ]);

  useEffect(() => {
    if (animateOn !== "view" && animateOn !== "both") return;

    const observerCallback = (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting && !hasAnimated) {
          setIsHovering(true);
          setHasAnimated(true);
        }
      });
    };

    const observerOptions = {
      root: null,
      rootMargin: "0px",
      threshold: 0.1,
    };

    const observer = new IntersectionObserver(observerCallback, observerOptions);
    const currentRef = containerRef.current;
    if (currentRef) {
      observer.observe(currentRef);
    }

    return () => {
      if (currentRef) {
        observer.unobserve(currentRef);
      }
    };
  }, [animateOn, hasAnimated]);

  const hoverProps =
    animateOn === "hover" || animateOn === "both"
      ? {
          onMouseEnter: () => setIsHovering(true),
          onMouseLeave: () => setIsHovering(false),
        }
      : {};

  return (
    <motion.span
      className={parentClassName}
      ref={containerRef}
      style={styles.wrapper}
      {...hoverProps}
      {...props}
    >
      <span style={styles.srOnly}>{displayText}</span>
      <span aria-hidden="true">
        {displayText.split("").map((char, index) => {
          const isRevealedOrDone =
            revealedIndices.has(index) || !isScrambling || !isHovering;

          return (
            <span key={index} className={isRevealedOrDone ? className : encryptedClassName}>
              {char}
            </span>
          );
        })}
      </span>
    </motion.span>
  );
}

import React from 'react';
import { AlertCircle, X } from 'lucide-react';

const ErrorModal = ({ error, onClose }) => {
  if (!error) return null;

  const getErrorIcon = (status) => {
    switch (status) {
      case 401:
      case 403:
        return 'ðŸ”';
      case 409:
        return 'âš ï¸';
      case 404:
        return 'âŒ';
      case 400:
        return 'ðŸ“';
      case 500:
        return 'ðŸ”¥';
      default:
        return 'âš ï¸';
    }
  };

  const getErrorTitle = (status, message) => {
    if (message) return message;
    switch (status) {
      case 400:
        return 'Invalid Input';
      case 401:
        return 'Unauthorized';
      case 403:
        return 'Forbidden';
      case 404:
        return 'Not Found';
      case 409:
        return 'Conflict';
      case 500:
        return 'Server Error';
      default:
        return 'Error';
    }
  };

  const getErrorDescription = (status, message) => {
    if (message && message !== getErrorTitle(status, message)) {
      return message;
    }
    
    switch (status) {
      case 400:
        return 'Please check your input and try again.';
      case 401:
        return 'Invalid credentials. Please check your password and try again.';
      case 403:
        return 'You do not have permission to access this resource.';
      case 404:
        return 'The requested resource was not found.';
      case 409:
        return 'This email is already registered. Please use a different email or log in.';
      case 500:
        return 'The server encountered an error. Please try again later.';
      default:
        return 'An unexpected error occurred. Please try again.';
    }
  };

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-2xl max-w-md w-full overflow-hidden animate-in fade-in zoom-in-95 duration-200">
        {/* Header */}
        <div className="bg-red-50 border-b border-red-200 px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <AlertCircle size={24} className="text-red-600" />
            <h2 className="text-lg font-semibold text-red-900">
              {getErrorTitle(error.status, error.title)}
            </h2>
          </div>
          <button
            type="button"
            onClick={onClose}
            className="p-1 hover:bg-red-100 rounded-lg transition text-red-600 hover:text-red-900"
          >
            <X size={20} />
          </button>
        </div>

        {/* Content */}
        <div className="px-6 py-4">
          <p className="text-gray-700 text-sm leading-relaxed">
            {getErrorDescription(error.status, error.message)}
          </p>

          {/* Status Code Badge */}
          {error.status && (
            <div className="mt-4 inline-block">
              <span className="bg-red-100 text-red-800 text-xs font-semibold px-3 py-1 rounded-full">
                Error {error.status}
              </span>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="bg-gray-50 px-6 py-3 flex gap-3 justify-end border-t border-gray-200">
          <button
            type="button"
            onClick={onClose}
            className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white font-medium rounded-lg transition"
          >
            Dismiss
          </button>
        </div>
      </div>
    </div>
  );
};

export default ErrorModal;

import { NavLink } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import {
    LayoutDashboard, Code, Trophy, User,
    Users, BookOpen, Swords, Megaphone, ShieldCheck,
    Building2, PlusCircle, ListChecks, BarChart2,
} from "lucide-react";

const playerLinks = [
    { to: "/dashboard", icon: LayoutDashboard, label: "Dashboard" },
    { to: "/practice", icon: Code, label: "Practice" },
    { to: "/contests", icon: Swords, label: "Contests" },
    { to: "/leaderboard", icon: Trophy, label: "Leaderboard" },
    { to: "/profile", icon: User, label: "Profile" },
];

const adminLinks = [
    { to: "/admin", icon: LayoutDashboard, label: "Overview" },
    { to: "/admin/users", icon: Users, label: "Users" },
    { to: "/admin/approve-hosts", icon: ShieldCheck, label: "Host Approvals" },
    { to: "/admin/company-approvals", icon: Building2, label: "Company Approvals" },
    { to: "/admin/problems", icon: BookOpen, label: "Problems" },
    { to: "/admin/create-problem", icon: PlusCircle, label: "New Problem" },
    { to: "/admin/contests", icon: Swords, label: "Contests" },
    { to: "/admin/create-contest", icon: PlusCircle, label: "New Contest" },
    { to: "/admin/announcements", icon: Megaphone, label: "Announcements" },
];

const hostLinks = [
    { to: "/host", icon: Building2, label: "Dashboard" },
    { to: "/host/problems", icon: BookOpen, label: "Problems" },
    { to: "/host/problems/create", icon: PlusCircle, label: "New Problem" },
    { to: "/host/contests/create", icon: PlusCircle, label: "New Contest" },
    { to: "/host/contests", icon: ListChecks, label: "My Contests" },
];

function SidebarLink({ to, icon: Icon, label, end: endProp }) {
    return (
        <NavLink
            to={to}
            end={endProp}
            className={({ isActive }) => `sidebar-link ${isActive ? "active" : ""}`}
        >
            <Icon size={16} />
            <span>{label}</span>
        </NavLink>
    );
}

export default function Sidebar() {
    const { role } = useAuth();
    const links = role === "ADMIN" ? adminLinks : role === "HOST" ? hostLinks : playerLinks;

    return (
        <aside className="w-56 shrink-0 border-r border-slate-700 flex flex-col" style={{ background: "#1E293B" }}>
            <nav className="flex-1 overflow-y-auto py-4 px-2 space-y-0.5">
                {links.map((l, i) => (
                    <SidebarLink
                        key={l.to}
                        {...l}
                        end={i === 0} // exact match for root-of-section links (dashboard, /admin, /host)
                    />
                ))}
            </nav>
        </aside>
    );
}

