import { createContext, useContext, useState, useEffect, useCallback } from "react";

const AuthContext = createContext(null);

const STORAGE_KEYS = {
    token: "token",
    user: "user",
};

// Decode JWT payload without a library
function parseJwt(token) {
    try {
        const base64 = token.split(".")[1].replace(/-/g, "+").replace(/_/g, "/");
        const jsonPayload = decodeURIComponent(
            atob(base64)
                .split("")
                .map((c) => "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2))
                .join("")
        );
        return JSON.parse(jsonPayload);
    } catch {
        return null;
    }
}

function readStoredAuth() {
    const localToken = localStorage.getItem(STORAGE_KEYS.token);
    const localUser = localStorage.getItem(STORAGE_KEYS.user);
    if (localToken && localUser) {
        return { token: localToken, user: localUser, storage: localStorage };
    }
    const sessionToken = sessionStorage.getItem(STORAGE_KEYS.token);
    const sessionUser = sessionStorage.getItem(STORAGE_KEYS.user);
    if (sessionToken && sessionUser) {
        return { token: sessionToken, user: sessionUser, storage: sessionStorage };
    }
    return null;
}

function clearStoredAuth() {
    localStorage.removeItem(STORAGE_KEYS.token);
    localStorage.removeItem(STORAGE_KEYS.user);
    sessionStorage.removeItem(STORAGE_KEYS.token);
    sessionStorage.removeItem(STORAGE_KEYS.user);
}

export function AuthProvider({ children }) {
    const [user, setUser] = useState(null);
    const [token, setToken] = useState(null);
    const [role, setRole] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    // Restore session on mount
    useEffect(() => {
        const stored = readStoredAuth();
        if (stored) {
            const payload = parseJwt(stored.token);
            if (payload && payload.exp && payload.exp * 1000 > Date.now()) {
                const parsedUser = JSON.parse(stored.user);
                setToken(stored.token);
                setUser(parsedUser);
                setRole(parsedUser.role);
            } else {
                clearStoredAuth();
            }
        }
        setIsLoading(false);
    }, []);

    const login = useCallback((tokenStr, userData, options = {}) => {
        // Block banned / suspended accounts
        if (userData.status === "BANNED") {
            throw new Error("Your account has been banned. Please contact support.");
        }
        if (userData.status === "SUSPENDED") {
            throw new Error("Your account is suspended. Please contact support.");
        }
        clearStoredAuth();
        const storage = options.remember ? localStorage : sessionStorage;
        storage.setItem(STORAGE_KEYS.token, tokenStr);
        storage.setItem(STORAGE_KEYS.user, JSON.stringify(userData));
        setToken(tokenStr);
        setUser(userData);
        setRole(userData.role);
    }, []);

    const logout = useCallback(() => {
        clearStoredAuth();
        setToken(null);
        setUser(null);
        setRole(null);
        window.location.href = "/login";
    }, []);

    // Update stored user (e.g., after profile fetch)
    const updateUser = useCallback((updatedData) => {
        const merged = { ...user, ...updatedData };
        const stored = readStoredAuth();
        const storage = stored?.storage ?? localStorage;
        storage.setItem(STORAGE_KEYS.user, JSON.stringify(merged));
        setUser(merged);
        setRole(merged.role);
    }, [user]);

    return (
        <AuthContext.Provider value={{ user, token, role, isLoading, login, logout, updateUser }}>
            {children}
        </AuthContext.Provider>
    );
}

export function useAuth() {
    const ctx = useContext(AuthContext);
    if (!ctx) throw new Error("useAuth must be used within AuthProvider");
    return ctx;
}

import { useEffect, useState, useCallback, useRef } from 'react';
import { socketService } from '../services/socketService';
import { useAuth } from '../context/AuthContext';

/**
 * Custom hook for managing WebSocket connections
 * @param {object} options - Configuration options
 * @param {number} options.contestId - Contest ID to join
 * @param {boolean} options.autoConnect - Auto-connect on mount (default: true)
 * @returns {object} WebSocket utilities
 */
export function useWebSocket({ contestId, autoConnect = true } = {}) {
    const { token } = useAuth();
    const [status, setStatus] = useState('disconnected');
    const [error, setError] = useState(null);
    const contestIdRef = useRef(contestId);
    
    // Update ref when contestId changes
    useEffect(() => {
        contestIdRef.current = contestId;
    }, [contestId]);

    // Auto-connect on mount
    useEffect(() => {
        if (!autoConnect || !token) return;

        setStatus('connecting');
        socketService.connect(
            token,
            () => {
                setStatus('connected');
                setError(null);
                
                // Join contest room if contestId provided
                if (contestIdRef.current) {
                    socketService.joinContest(contestIdRef.current);
                }
            },
            (err) => {
                setStatus('disconnected');
                setError(err.message || 'Connection failed');
            }
        );

        return () => {
            if (contestIdRef.current) {
                socketService.leaveContest(contestIdRef.current);
            }
            socketService.disconnect();
            setStatus('disconnected');
        };
    }, [autoConnect, token]);

    // Manual connect function
    const connect = useCallback(() => {
        if (!token) {
            setError('No authentication token');
            return;
        }

        setStatus('connecting');
        socketService.connect(
            token,
            () => {
                setStatus('connected');
                setError(null);
                
                if (contestIdRef.current) {
                    socketService.joinContest(contestIdRef.current);
                }
            },
            (err) => {
                setStatus('disconnected');
                setError(err.message || 'Connection failed');
            }
        );
    }, [token]);

    // Manual disconnect function
    const disconnect = useCallback(() => {
        if (contestIdRef.current) {
            socketService.leaveContest(contestIdRef.current);
        }
        socketService.disconnect();
        setStatus('disconnected');
    }, []);

    return {
        status,
        error,
        isConnected: status === 'connected',
        connect,
        disconnect,
        socketService, // Expose service for manual event subscriptions
    };
}

/**
 * Hook specifically for contest rooms with all live events
 * @param {number} contestId - Contest ID
 * @param {object} handlers - Event handlers
 * @returns {object} WebSocket utilities
 */
export function useContestWebSocket(contestId, handlers = {}) {
    const ws = useWebSocket({ contestId });

    useEffect(() => {
        if (!ws.isConnected) return;

        const unsubscribers = [];

        // Leaderboard updates
        if (handlers.onLeaderboardUpdate) {
            const unsub = socketService.subscribeToLeaderboard(
                contestId,
                handlers.onLeaderboardUpdate
            );
            unsubscribers.push(unsub);
        }

        // Problem solve notifications
        if (handlers.onProblemSolve) {
            const unsub = socketService.subscribeToProblemSolves(
                contestId,
                handlers.onProblemSolve
            );
            unsubscribers.push(unsub);
        }

        // Rank changes
        if (handlers.onRankChange) {
            const unsub = socketService.subscribeToRankChanges(
                contestId,
                handlers.onRankChange
            );
            unsubscribers.push(unsub);
        }

        // Contest status changes
        if (handlers.onStatusChange) {
            const unsub = socketService.subscribeToContestStatus(
                contestId,
                handlers.onStatusChange
            );
            unsubscribers.push(unsub);
        }

        // Level-up notifications
        if (handlers.onLevelUp) {
            const unsub = socketService.subscribeToLevelUp(
                handlers.onLevelUp
            );
            unsubscribers.push(unsub);
        }

        // Achievement unlocks
        if (handlers.onAchievement) {
            const unsub = socketService.subscribeToAchievements(
                handlers.onAchievement
            );
            unsubscribers.push(unsub);
        }

        // Cleanup all subscriptions
        return () => {
            unsubscribers.forEach(unsub => unsub());
        };
    }, [ws.isConnected, contestId, handlers]);

    return ws;
}

import React, { useState, useEffect } from 'react';
import { Users, AlertCircle, FileText, Trophy } from 'lucide-react';
import { adminService } from '../../services/adminService';
import { useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalPlayers: 0,
    totalHosts: 0,
    pendingApprovals: 0,
    activeContests: 0,
    totalProblems: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardStats();
  }, []);

