import { useState, useEffect } from "react";
import axiosInstance from "../../api/axiosInstance";
import Loader from "../../components/Loader";
import toast from "react-hot-toast";
import { CheckCircle, XCircle, Building2 } from "lucide-react";

export default function ApproveHosts() {
    const [hosts, setHosts] = useState([]);
    const [loading, setLoading] = useState(true);

    const load = () => {
        setLoading(true);
        axiosInstance.get("/admin/hosts")
            .then((r) => setHosts(r.data?.content ?? r.data ?? []))
            .catch((err) => {
                // Handle 404 gracefully - endpoint might not be implemented yet
                if (err.response?.status === 404) {
                    setHosts([]);
                } else {
                    toast.error("Failed to load hosts");
                }
            })
            .finally(() => setLoading(false));
    };

    useEffect(() => { load(); }, []);

    const approve = async (id) => {
        try {
            await axiosInstance.patch(`/admin/hosts/${id}/approve`);
            toast.success("Host approved");
            setHosts((prev) => prev.map((h) => h.id === id ? { ...h, status: "APPROVED" } : h));
        } catch { toast.error("Failed to approve host"); }
    };

    const reject = async (id) => {
        try {
            await axiosInstance.patch(`/admin/hosts/${id}/reject`);
            toast.success("Host rejected");
            load();
        } catch { toast.error("Failed to reject host"); }
    };

    return (
        <div className="space-y-5">
            <h1 className="text-2xl font-mono font-bold text-slate-100">Approve Hosts</h1>
            {loading ? <Loader /> : (
                <div className="card overflow-hidden p-0">
                    <table className="w-full">
                        <thead>
                            <tr className="border-b border-border">
                                <th className="table-header">Company</th>
                                <th className="table-header hidden md:table-cell">Type</th>
                                <th className="table-header hidden md:table-cell">Size</th>
                                <th className="table-header">Status</th>
                                <th className="table-header text-right">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {hosts.length === 0 && (
                                <tr><td colSpan={5} className="table-cell text-center py-8 text-slate-500">No host accounts</td></tr>
                            )}
                            {hosts.map((h) => (
                                <tr key={h.id} className="table-row">
                                    <td className="table-cell">
                                        <div className="flex items-center gap-2">
                                            <Building2 size={14} className="text-secondary" />
                                            <span className="font-medium text-slate-200">{h.company_name}</span>
                                        </div>
                                    </td>
                                    <td className="table-cell hidden md:table-cell text-slate-400">{h.type}</td>
                                    <td className="table-cell hidden md:table-cell text-slate-400">{h.company_size}</td>
                                    <td className="table-cell">
                                        <span className={`badge ${h.status === "APPROVED" ? "badge-success" : h.status === "PENDING" ? "badge-warning" : "badge-danger"}`}>
                                            {h.status}
                                        </span>
                                    </td>
                                    <td className="table-cell text-right">
                                        <div className="flex items-center justify-end gap-2">
                                            {h.status !== "APPROVED" && (
                                                <button onClick={() => approve(h.id)} className="btn-primary flex items-center gap-1 text-xs px-3 py-1.5">
                                                    <CheckCircle size={12} /> Approve
                                                </button>
                                            )}
                                            <button onClick={() => reject(h.id)} className="btn-danger flex items-center gap-1 text-xs px-3 py-1.5">
                                                <XCircle size={12} /> Reject
                                            </button>
                                        </div>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

import React, { useState, useEffect } from 'react';
import { problemService } from '../../services/problemService';
import { parseError } from '../../utils/errorHandler';
import ErrorModal from '../../components/ErrorModal';
import { toast } from 'react-hot-toast';
import { Plus, Trash2 } from 'lucide-react';

export default function CreateProblem() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    difficulty: 'MEDIUM',
    points: 100,
    time_limit_ms: 2000,
    memory_limit_mb: 256,
    tags: [],
    constraints: [],
    hints: [],
    example1: '',
    example2: '',
    example3: '',
    starter_code1: '', // Python
    starter_code2: '', // Java
    starter_code3: '', // JavaScript
    starter_code4: '', // C++
    test_cases: [],
    solution_code: '',
    is_ai_generated: false
  });

  const [loading, setLoading] = useState(false);
  const [modalError, setModalError] = useState(null);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleArrayChange = (e, index, field) => {
    const { value } = e.target;
    const arr = [...formData[field]];
    arr[index] = value;
    setFormData(prev => ({
      ...prev,
      [field]: arr
    }));
  };

  const addArrayItem = (field) => {
    setFormData(prev => ({
      ...prev,
      [field]: [...prev[field], '']
    }));
  };

  const removeArrayItem = (field, index) => {
    setFormData(prev => ({
      ...prev,
      [field]: prev[field].filter((_, i) => i !== index)
    }));
  };

  const addTestCase = () => {
    setFormData(prev => ({
      ...prev,
      test_cases: [...prev.test_cases, { input: '', output: '', is_sample: false }]
    }));
  };

  const removeTestCase = (index) => {
    setFormData(prev => ({
      ...prev,
      test_cases: prev.test_cases.filter((_, i) => i !== index)
    }));
  };

  const handleTestCaseChange = (index, key, value) => {
    const tc = [...formData.test_cases];
    tc[index][key] = value;
    setFormData(prev => ({ ...prev, test_cases: tc }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.title.trim()) {
      toast.error('Title is required');
      return;
    }

    if (!formData.description.trim()) {
      toast.error('Description is required');
      return;
    }

    if (formData.test_cases.length === 0) {
      toast.error('Please add at least one test case');
      return;
    }

    setLoading(true);
    try {
      const result = await problemService.createProblem(formData);
      const problemId = result.data?.id || result.data?.problemId || result.id || result.problemId;
      toast.success(problemId ? `Problem created successfully! ID: ${problemId}` : 'Problem created successfully!');
      
      // Reset form
      setFormData({
        title: '',
        description: '',
        difficulty: 'MEDIUM',
        points: 100,
        time_limit_ms: 2000,
        memory_limit_mb: 256,
        tags: [],
        constraints: [],
        hints: [],
        example1: '',
        example2: '',
        example3: '',
        starter_code1: '',
        starter_code2: '',
        starter_code3: '',
        starter_code4: '',
        test_cases: [],
        solution_code: '',
        is_ai_generated: false
      });
    } catch (err) {
      const parsedError = parseError(err);
      setModalError(parsedError);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white p-8">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-3xl font-bold text-slate-900 mb-8">ðŸ“ Create New Problem</h1>

        {modalError && <ErrorModal error={modalError} onDismiss={() => setModalError(null)} />}

        <form onSubmit={handleSubmit} className="space-y-8">
          
          {/* Basic Information */}
          <fieldset className="border-b-2 border-slate-200 pb-8">
            <legend className="text-xl font-bold text-slate-900 mb-6">ðŸ“ Basic Information</legend>
            
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Title *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  required
                  placeholder="e.g., Two Sum, Palindrome Check"
                  className="w-full px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400"
                />
              </div>

              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Description *</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  rows="6"
                  placeholder="Detailed problem statement..."
                  className="w-full px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400"
                />
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">Difficulty *</label>
                  <select name="difficulty" value={formData.difficulty} onChange={handleChange} className="w-full px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400">
                    <option value="EASY">EASY</option>
                    <option value="MEDIUM">MEDIUM</option>
                    <option value="HARD">HARD</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">Points</label>
                  <input
                    type="number"
                    name="points"
                    value={formData.points}
                    onChange={handleChange}
                    min="10"
                    max="1000"
                    className="w-full px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400"
                  />
                </div>

                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">Time Limit (ms)</label>
                  <input
                    type="number"
                    name="time_limit_ms"
                    value={formData.time_limit_ms}
                    onChange={handleChange}
                    min="100"
                    className="w-full px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Memory Limit (MB)</label>
                <input
                  type="number"
                  name="memory_limit_mb"
                  value={formData.memory_limit_mb}
                  onChange={handleChange}
                  min="32"
                  className="w-full px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400"
                />
              </div>
            </div>
          </fieldset>

          {/* Tags & Constraints */}
          <fieldset className="border-b-2 border-slate-200 pb-8">
            <legend className="text-xl font-bold text-slate-900 mb-6">ðŸ·ï¸ Tags & Constraints</legend>
            
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Tags</label>
                {formData.tags.map((tag, i) => (
                  <div key={i} className="flex gap-2 mb-2">
                    <input
                      type="text"
                      value={tag}
                      onChange={(e) => handleArrayChange(e, i, 'tags')}
                      placeholder="e.g., array, hashmap, sorting"
                      className="flex-1 px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400"
                    />
                    <button
                      type="button"
                      onClick={() => removeArrayItem('tags', i)}
                      className="px-3 py-2 bg-red-50 text-red-600 rounded-lg hover:bg-red-100 transition"
                    >
                      <Trash2 size={16} />
                    </button>
                  </div>
                ))}
                <button
                  type="button"
                  onClick={() => addArrayItem('tags')}
                  className="px-4 py-2 bg-yellow-50 text-yellow-700 rounded-lg hover:bg-yellow-100 transition font-semibold flex items-center gap-2"
                >
                  <Plus size={16} /> Add Tag
                </button>
              </div>

              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Constraints</label>
                {formData.constraints.map((c, i) => (
                  <div key={i} className="flex gap-2 mb-2">
                    <input
                      type="text"
                      value={c}
                      onChange={(e) => handleArrayChange(e, i, 'constraints')}
                      placeholder="e.g., 1 <= n <= 10^5"
                      className="flex-1 px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400"
                    />
                    <button
                      type="button"
                      onClick={() => removeArrayItem('constraints', i)}
                      className="px-3 py-2 bg-red-50 text-red-600 rounded-lg hover:bg-red-100 transition"
                    >
                      <Trash2 size={16} />
                    </button>
                  </div>
                ))}
                <button
                  type="button"
                  onClick={() => addArrayItem('constraints')}
                  className="px-4 py-2 bg-yellow-50 text-yellow-700 rounded-lg hover:bg-yellow-100 transition font-semibold flex items-center gap-2"
                >
                  <Plus size={16} /> Add Constraint
                </button>
              </div>

              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Hints (Optional)</label>
                {formData.hints.map((hint, i) => (
                  <div key={i} className="flex gap-2 mb-2">
                    <textarea
                      value={hint}
                      onChange={(e) => handleArrayChange(e, i, 'hints')}
                      rows="2"
                      placeholder="Hint for solving..."
                      className="flex-1 px-4 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400"
                    />
                    <button
                      type="button"
                      onClick={() => removeArrayItem('hints', i)}
                      className="px-3 py-2 bg-red-50 text-red-600 rounded-lg hover:bg-red-100 transition"
                    >
                      <Trash2 size={16} />
                    </button>
                  </div>
                ))}
                <button
                  type="button"
                  onClick={() => addArrayItem('hints')}
                  className="px-4 py-2 bg-yellow-50 text-yellow-700 rounded-lg hover:bg-yellow-100 transition font-semibold flex items-center gap-2"
                >
                  <Plus size={16} /> Add Hint
                </button>
              </div>
            </div>
          </fieldset>

          {/* Examples */}
          <fieldset className="border-b-2 border-slate-200 pb-8">
            <legend className="text-xl font-bold text-slate-900 mb-6">ðŸ“Œ Examples</legend>
            
            {[1, 2, 3].map(num => (
              <div key={num} className="mb-4">
                <label className="block text-sm font-semibold text-slate-700 mb-2">Example {num}</label>
                <textarea
                  name={`example${num}`}
                  value={formData[`example${num}`]}
                  onChange={handleChange}
                  rows="3"
                  placeholder={`Input: ...\nOutput: ...\nExplanation: ...`}
