import { useState, useEffect } from "react";
import axiosInstance from "../../api/axiosInstance";
import Loader from "../../components/Loader";
import toast from "react-hot-toast";
import { ShieldAlert, ShieldCheck, ShieldOff, UserX } from "lucide-react";

const STATUS_OPTIONS = ["ACTIVE", "BANNED", "SUSPENDED", "DORMANT"];

export default function ManageUsers() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [search, setSearch] = useState("");

    const load = () => {
        setLoading(true);
        axiosInstance.get("/admin/users", { params: search ? { search } : {} })
            .then((r) => setUsers(r.data?.content ?? r.data ?? []))
            .catch(() => toast.error("Failed to load users"))
            .finally(() => setLoading(false));
    };

    useEffect(() => { load(); }, []);

    const changeStatus = async (userId, status) => {
        try {
            await axiosInstance.put(`/admin/users/${userId}/status`, { status });
            toast.success(`Status updated to ${status}`);
            setUsers((prev) => prev.map((u) => u.id === userId ? { ...u, status } : u));
        } catch {
            toast.error("Failed to update status");
        }
    };

    const statusIcon = { ACTIVE: ShieldCheck, BANNED: ShieldOff, SUSPENDED: ShieldAlert, DORMANT: UserX };
    const statusCls = { ACTIVE: "text-success", BANNED: "text-danger", SUSPENDED: "text-warning", DORMANT: "text-slate-400" };

    return (
        <div className="space-y-5">
            <div className="flex items-center justify-between gap-3">
                <h1 className="text-2xl font-mono font-bold text-slate-100">Manage Users</h1>
                <div className="flex gap-2">
                    <input
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}
                        placeholder="Search users..."
                        className="input-field w-44"
                        onKeyDown={(e) => e.key === "Enter" && load()}
                    />
                    <button onClick={load} className="btn-secondary text-sm px-3">Search</button>
                </div>
            </div>

            {loading ? <Loader /> : (
                <div className="card overflow-hidden p-0">
                    <table className="w-full">
                        <thead>
                            <tr className="border-b border-border">
                                <th className="table-header">Name</th>
                                <th className="table-header hidden md:table-cell">Email</th>
                                <th className="table-header">Role</th>
                                <th className="table-header">Status</th>
                                <th className="table-header text-right">Change Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.length === 0 && (
                                <tr><td colSpan={5} className="table-cell text-center py-8 text-slate-500">No users found</td></tr>
                            )}
                            {users.map((u) => {
                                const Icon = statusIcon[u.status] ?? ShieldCheck;
                                return (
                                    <tr key={u.id} className="table-row">
                                        <td className="table-cell font-medium text-slate-200">{u.name}</td>
                                        <td className="table-cell hidden md:table-cell text-slate-400">{u.email}</td>
                                        <td className="table-cell"><span className="badge badge-primary">{u.role}</span></td>
                                        <td className="table-cell">
                                            <Icon size={14} className={`inline mr-1 ${statusCls[u.status]}`} />
                                            <span className={`text-sm ${statusCls[u.status]}`}>{u.status}</span>
                                        </td>
                                        <td className="table-cell text-right">
                                            <select
                                                value={u.status}
                                                onChange={(e) => changeStatus(u.id, e.target.value)}
                                                className="appearance-none bg-bg border border-border text-slate-300 text-xs rounded-input px-2 py-1 focus:outline-none focus:border-primary"
                                            >
                                                {STATUS_OPTIONS.map((s) => (
                                                    <option key={s} value={s}>{s}</option>
                                                ))}
                                            </select>
                                        </td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

import { useState } from "react";
import { announcementService } from "../../services/announcementService";
import axiosInstance from "../../api/axiosInstance";
import toast from "react-hot-toast";
import { Megaphone } from "lucide-react";

const TYPES = ["INFO", "WARNING", "SUCCESS", "DANGER"];
const TARGET_ROLES = ["ALL", "PLAYER", "HOST", "ADMIN"];

export default function PostAnnouncement() {
    const [form, setForm] = useState({
        title: "", message: "", type: "INFO",
        target_role: "ALL", target_contest: "",
        expires_at: "", is_active: true,
    });
    const [loading, setLoading] = useState(false);

    const set = (field, val) => setForm((f) => ({ ...f, [field]: val }));

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!form.title || !form.message) { toast.error("Title and message required"); return; }
        setLoading(true);
        try {
            await axiosInstance.post("/admin/announcements", form);
            toast.success("Announcement posted!");
            setForm({ title: "", message: "", type: "INFO", target_role: "ALL", target_contest: "", expires_at: "", is_active: true });
        } catch (err) {
            toast.error(err.response?.data?.message ?? "Failed to post announcement");
        } finally {
            setLoading(false);
        }
    };

    const typeCls = { INFO: "badge-primary", WARNING: "badge-warning", SUCCESS: "badge-success", DANGER: "badge-danger" };

    return (
        <div className="space-y-5 max-w-2xl">
            <div className="flex items-center gap-2">
                <Megaphone className="text-primary" size={22} />
                <h1 className="text-2xl font-mono font-bold text-slate-100">Post Announcement</h1>
            </div>

            <form onSubmit={handleSubmit} className="card space-y-4">
                <div>
                    <label className="block text-sm font-medium text-slate-300 mb-1.5">Title *</label>
                    <input value={form.title} onChange={(e) => set("title", e.target.value)} className="input-field" placeholder="System Maintenance" />
                </div>
                <div>
                    <label className="block text-sm font-medium text-slate-300 mb-1.5">Message *</label>
                    <textarea value={form.message} onChange={(e) => set("message", e.target.value)}
                        rows={4} className="input-field resize-y" placeholder="We will be performing maintenance..." />
                </div>
                <div className="grid grid-cols-2 gap-3">
                    <div>
                        <label className="block text-sm font-medium text-slate-300 mb-1.5">Type</label>
                        <div className="flex flex-wrap gap-1">
                            {TYPES.map((t) => (
                                <button key={t} type="button" onClick={() => set("type", t)}
                                    className={`badge ${typeCls[t]} cursor-pointer ${form.type === t ? "ring-1 ring-white/20" : "opacity-60"}`}>
                                    {t}
                                </button>
                            ))}
                        </div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-slate-300 mb-1.5">Target Role</label>
                        <select value={form.target_role} onChange={(e) => set("target_role", e.target.value)} className="input-field">
                            {TARGET_ROLES.map((r) => <option key={r} value={r}>{r}</option>)}
                        </select>
                    </div>
                </div>
                <div className="grid grid-cols-2 gap-3">
                    <div>
                        <label className="block text-sm font-medium text-slate-300 mb-1.5">Target Contest (optional)</label>
                        <input value={form.target_contest} onChange={(e) => set("target_contest", e.target.value)} className="input-field" placeholder="Contest ID" />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-slate-300 mb-1.5">Expires At (optional)</label>
                        <input type="datetime-local" value={form.expires_at} onChange={(e) => set("expires_at", e.target.value)} className="input-field" />
                    </div>
                </div>
                <div className="flex items-center gap-2">
                    <input type="checkbox" checked={form.is_active} onChange={(e) => set("is_active", e.target.checked)}
                        id="is_active" className="w-4 h-4 accent-primary" />
                    <label htmlFor="is_active" className="text-sm text-slate-300">Active immediately</label>
                </div>

                <button type="submit" disabled={loading} className="btn-primary px-8 py-2.5 flex items-center gap-2">
                    {loading && <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />}
                    <Megaphone size={14} />
                    Post Announcement
                </button>
            </form>
        </div>
    );
}

