function StarCanvas() {
    const canvasRef = useRef(null);
    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;
        const ctx = canvas.getContext("2d");
        let animId;

        const STAR_COUNT = 180;
        const COLORS = ["#ffffff", "#ffe9a0", "#ffd700", "#cd9f6a", "#d0d0d0"];

        function resize() {
            canvas.width = canvas.offsetWidth;
            canvas.height = canvas.offsetHeight;
        }
        resize();
        const ro = new ResizeObserver(resize);
        ro.observe(canvas);

        const stars = Array.from({ length: STAR_COUNT }, () => ({
            x: Math.random(),
            y: Math.random(),
            r: 0.4 + Math.random() * 1.4,
            color: COLORS[Math.floor(Math.random() * COLORS.length)],
            speed: 0.3 + Math.random() * 1.4,
            phase: Math.random() * Math.PI * 2,
            drift: (Math.random() - 0.5) * 0.00008,
        }));

        function draw(t) {
            const W = canvas.width;
            const H = canvas.height;
            ctx.clearRect(0, 0, W, H);
            for (const s of stars) {
                const alpha = 0.25 + 0.75 * (0.5 + 0.5 * Math.sin(t * 0.001 * s.speed + s.phase));
                ctx.save();
                ctx.globalAlpha = alpha;
                ctx.fillStyle = s.color;
                ctx.shadowBlur = s.r * 4;
                ctx.shadowColor = s.color;
                ctx.beginPath();
                ctx.arc(s.x * W, s.y * H, s.r, 0, Math.PI * 2);
                ctx.fill();
                ctx.restore();
                s.x = (s.x + s.drift + 1) % 1;
            }
            animId = requestAnimationFrame(draw);
        }
        animId = requestAnimationFrame(draw);
        return () => { cancelAnimationFrame(animId); ro.disconnect(); };
    }, []);
    return (
        <canvas
            ref={canvasRef}
            style={{
                position: "absolute", inset: 0,
                width: "100%", height: "100%",
                pointerEvents: "none",
            }}
        />
    );
}

function AnimatedNumber({ value, suffix = "", prefix = "", decimals = 0, duration = 2000 }) {
    const [count, setCount] = useState(0);
    const ref = useRef(null);

    useEffect(() => {
        let startTime;
        let animationFrame;
        let timeoutId;
        let isAnimating = false;

        const animate = (time) => {
            if (!startTime) startTime = time;
            const progress = Math.min((time - startTime) / duration, 1);
            const easeProgress = 1 - Math.pow(1 - progress, 3); // easeOutCubic

            setCount(value * easeProgress);

            if (progress < 1) {
                animationFrame = requestAnimationFrame(animate);
            } else {
                setCount(value);
                isAnimating = false;
                timeoutId = setTimeout(startAnimation, 3000);
            }
        };

        const startAnimation = () => {
            if (isAnimating) return;
            isAnimating = true;
            startTime = null;
            if (animationFrame) cancelAnimationFrame(animationFrame);
            if (timeoutId) clearTimeout(timeoutId);
            setCount(0);
            animationFrame = requestAnimationFrame(animate);
        };

        const observer = new IntersectionObserver(
            (entries) => {
                if (entries[0].isIntersecting) {
                    startAnimation();
                } else {
                    if (animationFrame) cancelAnimationFrame(animationFrame);
                    if (timeoutId) clearTimeout(timeoutId);
                    isAnimating = false;
                }
            },
            { threshold: 0.1 }
        );

        if (ref.current) {
            observer.observe(ref.current);
        }

        return () => {
            if (animationFrame) cancelAnimationFrame(animationFrame);
            if (timeoutId) clearTimeout(timeoutId);
            observer.disconnect();
        };
    }, [value, duration]);

    const formatted = count.toLocaleString(undefined, {
        minimumFractionDigits: decimals,
        maximumFractionDigits: decimals
    });

    return <span ref={ref}>{prefix}{formatted}{suffix}</span>;
}

